import React, { Fragment, useState } from 'react';

const Pregunta = () => {


    // 1. Definir el state

    const [ cantidad, guardarCantidad ] = useState(0);

    const [ error, actualizarError ] = (false) ;

    // 2. Funci贸n que lee el presupuesto. Evento cuando escribimos en el input.

    const definirPresupuesto = (e) => {
        guardarCantidad(parseInt(e.target.value, 10))
        }


    // 3. Submit para definir el presupuesto. Evento cuando damos a Submit

    const agregarPresupuesto = e => {
        e.preventDefault();
    }

    // 4. Validar

    if( cantidad < 1  || isNaN(cantidad)){
        actualizarError(true);
        return;
    }

    // 4. Pasa la validaci贸n




        return (
            <Fragment>
                <h2>Coloca tu presupuesto</h2>

                {error ? <p>La cantidad no es correcta</p> : <p>Vas bien</p>}

                <form
                    onSubmit={agregarPresupuesto}
                >
                    <input 
                        type= "number"
                        className="u-full-width"
                        placeholder="Coloca tu presupuesto"
                        onChange = {definirPresupuesto}
                    />

                    <input 
                        type="submit"
                        className="button-primary u-full-width"
                        value="Definir Presupuesto"
                    />
                </form>
            </Fragment>
        )
}

export default Pregunta
